import { setTimeout } from "timers";

let selectedHand : JQuery = null
let selectedPreview : JQuery = null


let deck: string[] = ["r4", "g7", "b2", "y9", "r8", "g3", "b6", "y3"]


function setHandClickHandler() {
    $("#handcards .handcard").off("click")
    $("#handcards .handcard").click(function(){

        
        if(selectedHand) {
            //sets scale of handcard to 1
            selectedHand.removeClass("selected")
        }

        if(!selectedHand || selectedHand.first().get(0) != $(this).get(0)) {
            selectedHand = $(this)
            //get cardtype
            const ct = selectedHand.data("cardtype")
            setPreviewCard(ct)

            selectedPreview.addClass("open")
            selectedHand.addClass("selected")

        }
        else {
            selectedHand = null
        }
        

    })
}

$(document).ready(() => {
    updateDeck()

    for(let i = 0; i < 4; i++) {
        addHandCard(deck.pop())
    }

    var socket = io.connect()
    //get canvas

    selectedPreview = $("#selected-card")

    setHandClickHandler()
    
    $("#selected-card").click(function() {
        //close selectedPreview if is clicked    
        selectedPreview.removeClass("open")

        //show hand card
        if(selectedHand) {
            //sets scale of handcard to 1
            selectedHand.removeClass("selected")
        }
    })


    $(".play").click(function() {
        //get type of card
        const ct = selectedPreview.data("cardtype")
        console.log("Playing card "+ct)
        //remove card from hand
        selectedHand.remove()
        addPlayedCard(ct)
    })

    socket.on("block", (data) => {
        
        
        
    })

    socket.emit("req", "tex1")


})


function updateDeck() {
    const Jdeck = $("#deck")
    Jdeck.children().remove()
    for(let i = 0; i < deck.length; i++) {
        const card = $("<div class='card deck-card'>")
        card.css("left", i*5)
        card.css("top", i*5)
        Jdeck.append(card)

        card.click(function() {
            //add card to hand, if playable: preview
            if(deck.length > 0) {
                let cardType = deck.pop()
                addHandCard(cardType)
                setHandClickHandler()
                updateDeck()
            }
        })
    }
}

function setDirection(clockwise : boolean) {
    const spinner = $(".spinner")
    const container = $(".spinner-div")
    container.addClass("zoom")

    window.setTimeout(() =>  {
        if(clockwise)spinner.removeClass("ccw")
        else spinner.addClass("ccw")
    }, 250)

    //remove zoom class
    window.setTimeout(() => {
        container.removeClass("zoom")
    }, 500)
    
}

function addPlayedCard(cardType: string) {

    
    const root = $("#played-cards")
    if(root.children().length > 6) {
        root.children().first().remove()
    }

    //Set transparency
    root.children().each((index, val) => {
        $(val).css("opacity", 1.0 - (index/10))
    })

    const card = $("<p class='card'>")
    if(cardType.charAt(0) == "r")card.css("background-color", "red")
    else if(cardType.charAt(0) == "g")card.css("background-color", "green")
    else if(cardType.charAt(0) == "b")card.css("background-color", "blue")
    else if(cardType.charAt(0) == "y")card.css("background-color", "yellow")

    card.text(cardType.charAt(1))
    card.css("transform", "rotate("+(Math.random()*50-25)+"deg)")
    root.append(card)
    
}

function addHandCard(cardType: string) {
    const root = $("#handcards")

    const card = $("<p class='card handcard'>")
    if(cardType.charAt(0) == "r")card.css("background-color", "red")
    else if(cardType.charAt(0) == "g")card.css("background-color", "green")
    else if(cardType.charAt(0) == "b")card.css("background-color", "blue")
    else if(cardType.charAt(0) == "y")card.css("background-color", "yellow")
    card.text(cardType.charAt(1))
    card.data("cardtype", cardType)
    root.append(card)
}

function setPreviewCard(cardType: string) {
    if(cardType.charAt(0) == "r")selectedPreview.css("background-color", "red")
    else if(cardType.charAt(0) == "g")selectedPreview.css("background-color", "green")
    else if(cardType.charAt(0) == "b")selectedPreview.css("background-color", "blue")
    else if(cardType.charAt(0) == "y")selectedPreview.css("background-color", "yellow")
    selectedPreview.find("#num").text(cardType.charAt(1))
    selectedPreview.data("cardtype", cardType)
}
